using namespace std;

int main()
{
    int n, s, m, curspace, cnt = 0;
    cin &gt;&gt; n &gt;&gt; s &gt;&gt; m;
    int board[n];

    for (int i = 0; i &lt; n; i++)
    {
        int tmp;
        cin &gt;&gt; tmp;
        board[i] = tmp;
    }
    curspace = s - 1;

    while (true)
    {
        if (curspace &lt; 0)
        {
            cout &lt;&lt; "left" &lt;&lt; endl;
            break;
        }
        else if (curspace &gt; n - 1)
        {
            cout &lt;&lt; "right" &lt;&lt; endl;
            break;
        }
        int temp = board[curspace];
        if (temp == 999)
        {
            cout &lt;&lt; "cycle" &lt;&lt; endl;
            break;
        }
        else if (temp == m)
        {
            cout &lt;&lt; "magic" &lt;&lt; endl;
            break;
        }
        else
        {
            cnt += 1;
            board[curspace] = 999;
            curspace += temp;
        }
    }
    cout &lt;&lt; cnt &lt;&lt; endl;
}
